import { Box, Grommet, Main } from 'grommet';

const LoginPage = () => {
  const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};
  return (
  <Grommet theme={theme}>
    <Box fill >
      <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
        <Box pad="medium" background={{
                "color": "neutral-1",
                "dark": true,
                "opacity": true,
                "position": "bottom",
                "repeat": "no-repeat",
                "size": "cover",
                "image": "url(https://storage.googleapis.com/coberry-ui/berries.jpg)",
          }}
        >

        </Box>
        <Box pad="small" background="light-3" />
      </Box>
      </Box>
  </Grommet>
)}

export default LoginPage;

