import React, { FunctionComponent } from 'react'; // importing FunctionComponent
import { Box } from 'Grommet';
import Link from 'next/link';

type Props = {}

const StyledHeader: FunctionComponent<Props> = ({
    children,
}) => (
    <Box
        tag='header'
        direction='row'
        align='center'
        justify='start'
        pad={{ left: 'medium', right: 'small', vertical: 'small' }}
        elevation='medium'
        style={{ zIndex: 1 }}
        border={{ color: 'primary', size: 'small' }}
        animation={"fadeIn"}
        gap={"small"}
        {...children}
    />
);

export default function Header() {
    return (
        <StyledHeader>
                <Link href="/">
                    <a>Home</a>
                </Link>{' '}
        </StyledHeader>
    )
}
