import { Box, Grommet, Main } from 'grommet';

const LoginPage = () => {
  const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};
  return (
  <Grommet theme={theme}>
    <Box basis="full" height="xxlarge" >
      <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
        <Box flex align='center' justify='center' background={{
                "color": "neutral-1",
                "dark": true,
                "opacity": true,
                "repeat": "no-repeat",
                "size": "cover",
                "image": "url(https://storage.googleapis.com/coberry-ui/berries.jpg)",
          }}
        >
        </Box>
        <Box pad="medium" align="center" justify="center" background="light-3">
        Sign in
        </Box>
      </Box>
      </Box>
  </Grommet>
)}

export default LoginPage;

